import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Auth.css'; // Import the CSS

function SignInPage() {
    const location = useLocation(); // Access URL query parameters
    const userType = new URLSearchParams(location.search).get('seller') ? 'seller' : 'buyer'; // Determine if the user is a seller or buyer

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle sign-in or sign-up logic here
    };

    return (
        <div className="auth-container">
            <h2>{userType === 'seller' ? 'Seller Sign In' : 'Buyer Sign In'}</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="username">Username</label>
                <input 
                    type="text" 
                    id="username" 
                    value={username} 
                    onChange={(e) => setUsername(e.target.value)} 
                    required 
                />
                
                <label htmlFor="password">Password</label>
                <input 
                    type="password" 
                    id="password" 
                    value={password} 
                    onChange={(e) => setPassword(e.target.value)} 
                    required 
                />
                
                <label htmlFor="confirm-password">Confirm Password</label>
                <input 
                    type="password" 
                    id="confirm-password" 
                    value={confirmPassword} 
                    onChange={(e) => setConfirmPassword(e.target.value)} 
                    required 
                />
                
                {error && <p className="error-message">{error}</p>}
                <button type="submit">Sign Up</button>
            </form>

            <p>
                Already have an account? <a href="/login">Login</a>
            </p>
        </div>
    );
}

export default SignInPage;
