import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import SignInPage from './components/SignInPage';
import LoginPage from './components/LoginPage';
import HomePage from './HomePage';

function App() {
  const [userType, setUserType] = useState(''); // To track whether it's buyer or seller
  const navigate = useNavigate();

  return (
    <Router>
      <div>
        <Routes>
          {/* Home Page */}
          <Route path="/" element={<HomePage />} />

          {/* Sign Up Pages for Seller and Buyer */}
          <Route path="/signup/seller" element={<SignInPage userType="seller" />} />
          <Route path="/signup/buyer" element={<SignInPage userType="buyer" />} />

          {/* Login Pages for Seller and Buyer */}
          <Route path="/login/seller" element={<LoginPage userType="seller" />} />
          <Route path="/login/buyer" element={<LoginPage userType="buyer" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
