import React, { useEffect, useState } from 'react';
import './BuyerInterface.css';
import logo from "../assets/images/BookTown.png";

const BuyerInterface = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/books');
        const data = await response.json();

        if (response.ok) {
          setBooks(data.books || []); // Default to empty array if no books
        } else {
          setError(data.message || 'Failed to fetch books');
        }
      } catch (err) {
        console.error('Error fetching books:', err);
        setError('There was an error fetching the books');
      }
    };

    fetchBooks();
  }, []);

  return (
    <div className="buyer-interface">
      <nav className="navbar">
        <div className="logo">
          <img src={logo} alt="Logo" />
        </div>
        <div className="navbar-buttons">
          <button onClick={() => window.location.href = '/events'}>Upcoming Events</button>
          <button onClick={() => window.location.href = '/'}>Home</button>
          <button onClick={() => window.location.href = '/'}>Logout</button>
          <button onClick={() => window.location.href = '/reviews'}>Reviews</button>
        </div>
      </nav>
      <h1>Available Books</h1>
      {error && <p className="error-message">{error}</p>}
      <div className="book-grid">
        {books.length === 0 && !error && <p>No books available</p>}
        {books.map((book, index) => (
          <div className="book-card" key={index}>
            <h2>{book.book_name || 'No Title Provided'}</h2>
            <p><strong>Author:</strong> {book.author || 'Unknown Author'}</p>
            <p><strong>Seller Email:</strong> {book.seller_email || 'Unknown Seller'}</p>
            <p><strong>Rental Period:</strong> {book.rental_period || 'Not Specified'}</p>
            <p><strong>City:</strong> {book.seller_city || 'Not Provided'}</p>
            <p><strong>State:</strong> {book.state_of_book || 'Not Mentioned'}</p>
            {book.state_of_book === 'Collectible' && (
              <>
                <p><strong>Print Run Number:</strong> {book.print_run_number || 'N/A'}</p>
                <p><strong>Paperback Type:</strong> {book.paperback_type || 'N/A'}</p>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BuyerInterface;
