import React, { useState, useEffect } from 'react';
import './EventsPage.css';
import logo from "../assets/images/BookTown.png";

const EventsPage = () => {
  const [isFormVisible, setFormVisible] = useState(false);
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    eventName: '',
    eventDescription: '',
    eventLocation: ''
  });

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/events');
        const data = await response.json();
        console.log('Fetched Events:', data); // Debugging
        if (response.ok) {
          setEvents(data.events);
        } else {
          console.error('Error fetching events:', data.message);
        }
      } catch (err) {
        console.error('Error:', err);
      }
    };

    fetchEvents();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (formData.eventName && formData.eventDescription && formData.eventLocation) {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/events', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData),
        });
        const newEvent = await response.json();
        console.log('New Event:', newEvent); // Debugging
        if (response.ok) {
          setEvents([...events, newEvent]); // Append new event
          setFormData({ eventName: '', eventDescription: '', eventLocation: '' });
          setFormVisible(false);
        }
      } catch (err) {
        console.error('Error:', err);
      }
    }
  };

  return (
    <div className="events-page">
      <nav className="navbar">
        <img src={logo} alt="Logo" className="logo" />
        <button className="upload-event-button" onClick={() => setFormVisible(!isFormVisible)}>
          Upload Event
        </button>
      </nav>

      {isFormVisible && (
        <div className="event-form">
          <form onSubmit={handleFormSubmit}>
            <label>
              Event Name:
              <input
                type="text"
                name="eventName"
                value={formData.eventName}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Event Description:
              <textarea
                name="eventDescription"
                value={formData.eventDescription}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Event Location:
              <input
                type="text"
                name="eventLocation"
                value={formData.eventLocation}
                onChange={handleInputChange}
                required
              />
            </label>
            <button type="submit">Submit</button>
          </form>
        </div>
      )}

      <div className="event-cards">
        {events.length > 0 ? (
          events.map((event, index) => (
            <div className="event-card" key={index}>
              <h3>{event.eventName}</h3>
              <p><strong>Description:</strong> {event.eventDescription}</p>
              <p><strong>Location:</strong> {event.eventLocation}</p>
            </div>
          ))
        ) : (
          <p>No events available.</p>
        )}
      </div>
    </div>
  );
};

export default EventsPage;
