import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Auth.css';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  // Determine user type based on the route
  const isSeller = location.pathname.includes('seller');

  const handleLogin = async (e) => {
    e.preventDefault();
    setSuccessMessage('');
    setError('');

    try {
      const response = await fetch('http://127.0.0.1:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username,
          password,
          userType: isSeller ? 'seller' : 'buyer', // Specify user type
        }),
      });

      const result = await response.json();

      if (response.ok) {
        setSuccessMessage(result.message);
        setTimeout(() => {
          navigate(isSeller ? '/seller-interface' : '/buyer-interface'); // Redirect to correct interface
        }, 1000);
      } else {
        setError(result.message || 'Login failed');
      }
    } catch (err) {
      setError('Error connecting to the server');
    }
  };

  return (
    <div className="auth-container">
      <h1>{isSeller ? 'Seller Login' : 'Buyer Login'}</h1> {/* Dynamic heading */}
      <form className="auth-form" onSubmit={handleLogin}>
        <label>{isSeller ? 'Seller Username' : 'Buyer Username'}</label>
        <input
          type="text"
          placeholder={`Enter your ${isSeller ? 'seller' : 'buyer'} username`}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <label>Password</label>
        <input
          type="password"
          placeholder={`Enter your ${isSeller ? 'seller' : 'buyer'} password`}
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        {error && <p className="error-message">{error}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}

        <button type="submit">{isSeller ? 'Seller Log In' : 'Buyer Log In'}</button>
      </form>
    </div>
  );
};

export default LoginPage;
