import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Auth.css';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState(''); // Added state for success message
  const navigate = useNavigate();
  const location = useLocation(); // To determine if it's seller or buyer login

  const isSeller = location.pathname.includes('seller'); // Check the path to identify seller/buyer

  const handleLogin = async (e) => {
    e.preventDefault();
    setSuccessMessage(''); // Reset success message on each attempt
    setError(''); // Reset error message on each attempt

    try {
      const response = await fetch('http://127.0.0.1:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username,
          password,
          userType: isSeller ? 'seller' : 'buyer', // Pass user type to backend
        }),
      });

      const result = await response.json();

      if (response.ok) {
        // If login is successful, show success message and navigate
        setSuccessMessage(result.message); // Show success message
        setTimeout(() => {
          if (isSeller) {
            navigate('/seller-interface'); // Redirect to Seller Interface for sellers
          } else {
            navigate('/buyer-interface'); // Redirect to Buyer Interface for buyers
          }
        }, 1000); // Delay for 1 second to show the success message
      } else {
        // If login fails, show error message
        setError(result.message || 'Login failed');
      }
    } catch (err) {
      setError('Error connecting to the server');
    }
  };

  return (
    <div className="auth-container">
      <h1>{isSeller ? 'Seller' : 'Buyer'} Login</h1>
      <form className="auth-form" onSubmit={handleLogin}>
        <label>Username</label>
        <input
          type="text"
          placeholder="Enter your username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <label>Password</label>
        <input
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        {error && <p className="error-message">{error}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>} {/* Display success message */}

        <button type="submit">Log In</button>
      </form>
    </div>
  );
};

export default LoginPage;
