import React, { useEffect, useState } from 'react';
import './BuyerInterface.css';
import logo from "../assets/images/BookTown.png";

const BuyerInterface = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/books');
        const data = await response.json();

        if (response.ok) {
          setBooks(data.books);
        } else {
          setError(data.message || 'Failed to fetch books');
        }
      } catch (err) {
        console.error('Error fetching books:', err);
        setError('There was an error fetching the books');
      }
    };

    fetchBooks();
  }, []);

  return (
    <div className="buyer-interface">
      <nav className="navbar">
        <div className="logo">
          <img src="/path/to/logo.png" alt="Logo" /> {/* Update with actual logo path */}
        </div>
        <div className="navbar-buttons">
          <button onClick={() => window.location.href = '/HomePage'}>Home</button>
          <button onClick={handleLogout}>Logout</button>
          <button onClick={handleNotifications}>Notifications</button>
        </div>
      </nav>
      <h1>Available Books</h1>
      {error && <p className="error-message">{error}</p>}
      <div className="book-grid">
        {books.map((book, index) => (
          <div className="book-card" key={index}>
            <h2>{book.bookName}</h2>
            <p><strong>Author:</strong> {book.author}</p>
            <p><strong>Seller:</strong> {book.sellerName}</p>
            <p><strong>Rental Period:</strong> {book.rentalPeriod}</p>
            <p><strong>City:</strong> {book.city}</p>
            <p><strong>State:</strong> {book.state}</p>
            {book.state === 'Collectible' && (
              <>
                <p><strong>Print Run Number:</strong> {book.printRunNumber}</p>
                <p><strong>Paperback Type:</strong> {book.paperbackType}</p>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BuyerInterface;
