import React, { useState } from 'react';
import './EventsPage.css';
import logo from "../assets/images/BookTown.png"; // Logo path (update accordingly)

const EventsPage = () => {
  const [isFormVisible, setFormVisible] = useState(false);
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    eventName: '',
    eventDescription: '',
    eventLocation: ''
  });

  // Handle changes in the form inputs
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (formData.eventName && formData.eventDescription && formData.eventLocation) {
      setEvents([...events, formData]); // Add new event to the events list
      setFormData({ eventName: '', eventDescription: '', eventLocation: '' });
      setFormVisible(false); // Hide the form after submission
    }
  };

  return (
    <div className="events-page">
      {/* Navbar */}
      <nav className="navbar">
        <img src={logo} alt="Logo" className="logo" />
        <button className="upload-event-button" onClick={() => setFormVisible(true)}>
          Upload Event
        </button>
      </nav>

      {/* Event Form */}
      {isFormVisible && (
        <div className="event-form">
          <form onSubmit={handleFormSubmit}>
            <label>
              Event Name:
              <input
                type="text"
                name="eventName"
                value={formData.eventName}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Event Description:
              <textarea
                name="eventDescription"
                value={formData.eventDescription}
                onChange={handleInputChange}
                required
              />
            </label>
            <label>
              Event Location:
              <input
                type="text"
                name="eventLocation"
                value={formData.eventLocation}
                onChange={handleInputChange}
                required
              />
            </label>
            <button type="submit">Submit</button>
          </form>
        </div>
      )}

      {/* Event Cards */}
      <div className="event-cards">
        {events.map((event, index) => (
          <div className="event-card" key={index}>
            <h3>{event.eventName}</h3>
            <p><strong>Description:</strong> {event.eventDescription}</p>
            <p><strong>Location:</strong> {event.eventLocation}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
