import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './Auth.css';

const SignInPage = () => {
  const { userType } = useParams();  // Get the userType from URL (seller/buyer)
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');  // Added state for success message
  const navigate = useNavigate();

  const handleSignIn = async (e) => {
    e.preventDefault();
    setSuccessMessage('');  // Reset success message on each attempt
    setError('');           // Reset error message on each attempt

    if (password !== confirmPassword) {
      setError("Passwords don't match");
      return;
    }

    try {
      const response = await fetch('http://127.0.0.1:5000/api/signin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username,
          password,
          userType,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        // If sign-in is successful, show success message and navigate
        setSuccessMessage(result.message);  // Show success message
        setTimeout(() => {
          navigate('/login');  // Redirect to login page after a short delay
        }, 1000);  // Delay for 1 second to show the success message
      } else {
        // If sign-in fails, show error message
        setError(result.message || 'Sign-in failed');
      }
    } catch (err) {
      setError('Error connecting to the server');
    }
  };

  return (
    <div className="auth-container">
      <h1>{userType.charAt(0).toUpperCase() + userType.slice(1)} Sign In</h1>
      <form className="auth-form" onSubmit={handleSignIn}>
        <label>Username</label>
        <input
          type="text"
          placeholder="Enter your username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <label>Password</label>
        <input
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <label>Confirm Password</label>
        <input
          type="password"
          placeholder="Confirm your password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
        />

        {error && <p className="error-message">{error}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}  {/* Display success message */}

        <button type="submit">Sign In</button>
      </form>

      <p className="redirect-text">
        Already have an account? <a href="/login">Log In</a>
      </p>
    </div>
  );
};

export default SignInPage;
