import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';

function SignInPage({ userType }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      setError("Passwords don't match");
      return;
    }

    // Check if username already exists
    const userExists = false; // Assume API call to check if the username exists
    if (userExists) {
      setError('Username already exists.');
      return;
    }

    setIsLoading(true);

    // Example API call to sign up user (hashing password before sending to backend)
    const hashedPassword = password; // Replace with actual hashing logic
    const response = await fetch(`/api/signup`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username,
        password: hashedPassword,
        userType,
      }),
    });

    const data = await response.json();

    if (data.success) {
      navigate(`/login/${userType}`);
    } else {
      setError(data.message || 'An error occurred');
    }

    setIsLoading(false);
  };

  return (
    <div className="auth-container">
      <h2>Sign Up as {userType}</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        <div className="input-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="confirm-password">Confirm Password</label>
          <input
            type="password"
            id="confirm-password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Signing Up...' : 'Sign Up'}
        </button>
      </form>
      <p>
        Already have an account?{' '}
        <button onClick={() => navigate(`/login/${userType}`)}>Login</button>
      </p>
    </div>
  );
}

export default SignInPage;
