import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EventsPage.css';
import logo from "../assets/images/BookTown.png"; 

const EventsPage = () => {
  const [isFormVisible, setFormVisible] = useState(false);
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    eventName: '',
    eventDescription: '',
    eventLocation: '',
    date: '',
    timings: ''
  });

  useEffect(() => {
    axios.get('http://localhost:5000/api/events')
      .then(response => setEvents(response.data))
      .catch(error => console.error('Error fetching events:', error));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:5000/api/events', formData)
      .then(response => {
        setEvents([...events, formData]); 
        setFormData({ eventName: '', eventDescription: '', eventLocation: '', date: '', timings: '' }); // Clear form
        setFormVisible(false); 
      })
      .catch(error => console.error('Error adding event:', error));
  };

  return (
    <div className="events-page">
      <nav className="navbar">
        <img src={logo} alt="Logo" className="logo" />
        <button className="upload-event-button" onClick={() => setFormVisible(!isFormVisible)}>
          {isFormVisible ? 'Close Form' : 'Upload Event'}
        </button>
        {isFormVisible && (
          <form className="event-form" onSubmit={handleFormSubmit}>
            <input
              type="text"
              name="eventName"
              placeholder="Event Name"
              value={formData.eventName}
              onChange={handleInputChange}
              required
            />
            <input
              type="text"
              name="eventDescription"
              placeholder="Event Description"
              value={formData.eventDescription}
              onChange={handleInputChange}
              required
            />
            <input
              type="text"
              name="eventLocation"
              placeholder="Event Location"
              value={formData.eventLocation}
              onChange={handleInputChange}
              required
            />
            <input
            type="date"
            name="date"
            placeholder="Event Date"
            value={formData.date}
            onChange={handleInputChange}
            required
            />
            <input
            type="text"
            name="timings"
            placeholder="Event Timings"
            value={formData.timings}
            onChange={handleInputChange}
            required
            />
            <button type="submit">Submit</button>
          </form>
        )}
      </nav>

      <h1 className="events-heading">Events</h1>
      <div className="event-cards">
        {events.map((event, index) => (
          <div className="event-card" key={index}>
            <h3>{event[0]}</h3>
            <p><strong>Description:</strong> {event[1]}</p>
            <p><strong>Date:</strong> {new Date(event[2]).toLocaleDateString()}</p> {/* Event Date */}
      <p><strong>Timings:</strong> {event[3]}</p> {/* Event Timings */}
            <p><strong>Location:</strong> {event[2]}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
